import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { useState } from 'react';

import { addFav, removeFav } from '../../redux/actions/actions';
import Styles from './Card.module.css';

export function Card({ id, name, species,
                       genre, image, onClose,
                       addFav, removeFav}) {
  const [isFav, setFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      setFav(false);
      removeFav(id);
    } else {
      setFav(true);
      addFav({ id, name, species, genre, image, onClose });
    }
  }

  return (
    <div className={Styles.card}>
      <div className={Styles.containerImg}>
        <button
          className={Styles.btn}
          onClick={onClose}
        >X</button>
        <img src={image} alt={id} className={Styles.img} />
      </div>
      { (isFav) ? (
          <button
            className={Styles.fav}
            onClick={handleFavorite}>❤</button>
            ) : (
          <button
            className={Styles.noFav}
            onClick={handleFavorite}>❤︎</button>
        )}

      <div>
        <Link to={`/detail/${id}`}>
          <h2>{name}</h2>
        </Link>
        <h2>{species}</h2>
        <h2>{genre}</h2>
      </div>
    </div>
  );
}


export function mapDispatchToProps(dispatch) {
  return {
    addFav: character => dispatch(addFav(character)),
    removeFav: id => dispatch(removeFav(id)),
  };
}

export default connect(null, mapDispatchToProps)(Card);
